"use strict"
angular.module("mean-factory-interceptor",[]).factory("httpInterceptor",["$q","$location",function(t,e){return{response:function(n){return 401===n.status?(e.path("/login"),t.reject(n)):n||t.when(n)},responseError:function(n){return 401===n.status?(e.url("/login"),t.reject(n)):t.reject(n)}}}]).config(["$httpProvider",function(t){t.interceptors.push("httpInterceptor")}])
